import React from "react";
import { useState } from "react";
import "./Newblog.css"
function Newblog(){
    const[blog,setBlog]=useState('');

    function handleChange(event){
        setBlog(event.target.value);
        console.log(blog);
    }

    function handlesubmit(){
        console.log(blog);
    }
    return(
        <div className="base_div">
            <div className="heading">
            ‚å® Start Blogging!!!
            </div>
                <textarea type="text" name="bloginput" value={blog} placeholder="max 10 lines"  className="bloginput" onChange={handleChange}/><br></br>
                <textarea type="text" name="name" placeholder="Name"  className="nameinput"/>
            <div><button className="submit" onClick={handlesubmit} >Submit</button></div>

        </div>
)}
export default Newblog
